// Extract user ID from Slack event
const userId = $input.item.json.user;

// Define authorized users with names (MUST use 'users' variable name)
const users = {
  'U1234ABCD': 'bot_user',
  'U9999XXXX': 'admin_user'
};

// Validate user authorization using hasOwnProperty
if (!users.hasOwnProperty(userId)) {
  throw new Error(`Access denied: User ${userId} is not authorized to use this workflow`);
}

// Return enriched data for authorized users
return {
  ...($input.item.json),
  authorizedUser: users[userId],
  isAuthorized: true,
  timestamp: new Date().toISOString()
};
