// Get user ID from Slack event data
const userId = $input.item.json.user;

// Define authorized users with permissions
const users = {
  'U1234ABCD': { name: 'bot_user', permissions: ['execute'] },
  'U9999XXXX': { name: 'readonly_user', permissions: ['read'] }
};

// Check authorization using bracket access
if (!users[userId]) {
  return {
    error: true,
    message: `User ${userId} is not authorized to access this workflow`,
    statusCode: 403
  };
}

// Check if user has execute permission
if (!users[userId].permissions.includes('execute')) {
  return {
    error: true,
    message: `User ${users[userId].name} does not have execute permissions`,
    statusCode: 403
  };
}

return {
  ...($input.item.json),
  authorizedUser: users[userId],
  isAuthorized: true,
  validationMethod: 'bracket_access'
};
