// Get user ID from Slack trigger
const userId = $input.item.json.user;

// INVALID: Define allowed users as array (arrays are not supported)
const allowedUsers = [
  'U5678EFGH',
  'U1234ABCD',
  'U9999XXXX',
  'U8888YYYY'
];

// Check if user is in allowed list
if (!allowedUsers.includes(userId)) {
  return {
    error: true,
    message: `Unauthorized access attempt by user: ${userId}`,
    statusCode: 403,
    userId: userId
  };
}

// User is authorized, proceed
return {
  ...($input.item.json),
  isAuthorized: true,
  userId: userId,
  validationMethod: 'array_includes'
};
