// BAD EXAMPLE: No user authorization check
// This directly processes the request without validating the user

const userId = $input.item.json.user;
const message = $input.item.json.text;

// Directly process without authorization check
console.log(`Processing request from user ${userId}`);

return {
  userId: userId,
  message: message,
  processed: true,
  timestamp: new Date().toISOString(),
  // WARNING: No authorization check performed!
  securityNote: 'This workflow accepts requests from any user'
};
